import React from "react";
import { Button } from "react-bootstrap";
import "./ChatInterface.css";

const send = () => {
  let message = document.getElementById("messageBox")
  console.log(message.value);

};

const ChatInterface = () => {
  return (
    <>
      <div className="discussionBox parent">
        <div className="col-sm-4 userBox userText">
          <p className="text-center p-3">Active Users</p>
          <p className="text-center ">Topics</p>
          <Button className="p-2 d-flex justify-content-center align-items-center">#General</Button>
          <Button className="p-2 d-flex text-center">#Art</Button>
          <Button className="p-2 text-center">#Sports</Button>
          <Button className="p-2 d-flex text-center">#Music</Button>

        </div>

        <div className="flex child">
          <div></div>
          <div>
            <div className="input-group">
              <textarea
                id="messageBox"
                type="text" 
                className="form-control messageBox"
                rows="3"
                cols="500"
                placeholder="Type Your Message"
              />
            </div>
          </div>
          <Button type="button" className="btn" onClick={send}>
            SEND
          </Button>
        </div>
      </div>
    </>
  );
};

export default ChatInterface;
