const handleSubmit = (e) => {
  e.preventDefault();
  e.target.reset();
};

const ChatInterface = () => {

  function displayTime() {
    const date = new Date();
    const hours = date.getHours().toString().padStart(2, 0);
    const newHour = (hours % 12 || 12).toString().padStart(2, 0);
    const minutes = date.getMinutes().toString().padStart(2, 0);


    const amPM = hours < "12" || hours === "24" ? "AM" : "PM";

    return (`${newHour}:${minutes} ${amPM}`) 
  }

  let theTime = displayTime()

  let userEmily = {
    name: 'Emily Thorne',
    messages: [
      {text: "Some say that our lives are defined by the sum of our choices.\
      But it isn’t really our choices that distinguish who we are. \
      It’s our commitment to them."},
      {text: "Confucus once said, 'before you embark on a journey of revenge, dig two graves'"}
  ]
  }

  let userSelf = {
    name: "You",
    messages: [
      { text: "Wow...that's a little deep for an early morning, is it not?"},
      { text: "Let's get breakfast at the Stowaway!"}
    ]
  }

  let users = [ userEmily, userSelf]

 const theUsers =  users.map( (user) => {

return (
    <div className="chat-container">
    <div
      className="card-header d-flex justify-content-between p-3"
      style={{ borderBottom: "1px solid rgba(255, 255, 255, 0.3" }}
    >
      <p className="fw-bold mb-0">{user.name}</p>
      <p className=" small mb-0">
        <i>{theTime}</i>

      </p>
    </div>
    <div className="card-body">
      <p className="mb-0 mx-auto p-2">{}</p>
    </div>
  </div>
)

  })

  return (
    <>
      <div id="container">
        <div className="userBox col-3 userText">
          <p className="text-center p-3">Active Users</p>
          <p className="text-center ">Topics</p>
          <Button
            className="p-2 d-flex justify-content-center align-items-center mx-auto mb-4 active"
            active
          >
            General
          </Button>
          <Button className="p-2 d-flex text-center mx-auto mb-4">Art</Button>
          <Button className="p-2 d-flex text-center mx-auto mb-4">
            Sports
          </Button>
          <Button className="p-2 d-flex text-center mx-auto mb-5">Music</Button>

          <Button
            className="p-3 d-flex text-center mx-auto logoutBtn"
            as={Link}
            to="/"
          >
            Logout
          </Button>
        </div>



        <div className="parent">
          <div className="discussionBox"  id="chatBox">

          {/* the message container  */}
          current Date
          {theUsers}

          </div>



          <div className="flex child">
            <div>
              <div className="input-group">
                <textarea
                  id="messageBox"
                  type="text"
                  className="form-control messageBox"
                  rows="2"
                  cols="500"
                  placeholder="Type Your Message"
                  onKeyUp={(e) => {
                    if (e.key === "Enter") {
                      send();
                      e.target.value = "";
                    }
                  }}
                  onSubmit={handleSubmit}
                />
              </div>
            </div>
            <Button type="submit" className="btn" onClick={send}>
              SEND
            </Button>
          </div>
        </div>
      </div>
    </>
  )

}


export default ChatInterface;
